  text="new text"; // Wrong way to do it
  setText
  return (
    <>
      <div>
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <textarea
            className="form-control"
            id="myBox"
            rows="8"
            value={text}
          ></textarea>
        </div>
        <div className="button btn btn-primary">Convert to upper case</div>
      </div>
    </>
  );
}
